import React from 'react';
import Form from './Form';
import Results from './Results';
import getDataFromApi from '../services/getDataFromApi';
import '../stylesheets/App.css';

class App extends React.Component {
  constructor (props) {
    super(props);
    this.getInfoFromApi = this.getInfoFromApi.bind(this);
    this.state = {
      userInfo: {
        name: '',
        photo: '',
        city: '',
        age: '',
        gender: ''
      }
    }
    this.getInfoFromApi();
  }
  getInfoFromApi () {
    getDataFromApi()
    .then(data =>
       //console.log(data)
       data.map( data => this.setState((prevState) => {
         return {
           userInfo: {
            ...prevState.userInfo,
            name: data.name.first,
            photo: data.medium,
            city: data.city,
            age: data.age,
            gender: data.gender
         }
        }
       })
    )
    );
  }

  // saveDataState () {
  //   this.setState ({
  //     name: data.name,
  //     photo: data.photo,
  //     city: data.city,
  //     age: data.age,
  //     gender: data.gender
  //   }),
  // }

  render() {
    return (
    <div className="App">
      <div>
        <Form handleInfo={this.getInfoFromApi}/>
      </div>
      <div>
        <Results info={this.state} />
      </div>
      </div>
    )
  }
}

export default App;
