  constructor(props) {
    super(props);
    this.state = {
      users: [],
      cities: [],
      gender: []
    }
    this.getInfoFromApi = this.getInfoFromApi.bind(this);
    this.getInfoFromApi();
  }
  getInfoFromApi () {
    getDataFromApi()
    .then(data =>
       //console.log(data[0].dob.age)
