      const newUser = {
        name: data.name.first,
        photo: data.picture.large,
        city: data.location.city,
        age: data.dob.age,
        gender: data.gender
      }
      //se repite 50 veces en el estado, ¿es eficiente o hay otra forma de hacerlo?
    return {
      users: [...this.state.users, newUser],
    }
    }))
    );
  }

  handleCities(city) {
    console.log(this.state);
    let users = this.state.users;
    const cities = (this.state.users.filter(user => user.city === city));
    console.log(cities);
  }

  render() {

return (
    <div className="App">
      <div>
        <Form
        handleCities={this.handleCities}
        userInfo={this.state}
        />
      </div>
      <div>
        <Results userInfo={this.state} />
      </div>
      </div>
    )
  }
}

export default App;
