import React from 'react';
import Item from './Item';


class App extends React.Component {
  render() {

    const arrayOfItems = [
      {
        name: 'Cereales con chocolate',
        description: 'Cereales rellenos de chocolate',
        quantity: 2,
        category: 'Cereales',
        price: 5
      },
      {
        name: 'Hamburguesa con queso',
        description: 'Hamburguesa rica y saludable',
        quantity: 1,
        category: 'Fast-food',
        price: 15
      },
      {
        name: 'Agua mineral',
        description: 'Agua de un charco del Himalaya',
        quantity: 2,
        category: 'Bebida',
        price: 5
      }
    ];
    console.log(arrayOfItems[0]);
  for(let i of arrayOfItems) {
    <li>
      <Item
        name="Cereales con chocolate"
        description="Cereales rellenos de chocolate"
        quantity={2}
        category="Cereales"
        price={5}
      />
    </li>

  }
    return ( 


    <ul className="item-list">
        <li>
          <Item
            name="Cereales con chocolate"
            description="Cereales rellenos de chocolate"
            quantity={2}
            category="Cereales"
            price={5}
          />
        </li>
        <li>
          <Item
            name="Hamburguesa con queso"
            description="Hamburguesa rica y saludable"
            quantity={1}
            category="Fast-food"
            price={15}
          />
        </li>
        </ul>
    );
  }
}

export default App;
