import React from 'react';
class OnionHater extends React.Component {
  constructor() {
    super();
    this.isHating = false;
    this.forceUpdate();
  }

  render() {
      const onChangeListener = ev => {
        //let text = ev.target.value;
        if(ev.target.value.includes('cebolla')){
            alert('ODIO LA CEBOLLA');
            this.isHating = true; 
        }  else {
          this.isHating = false; 
        } 
        console.log(this.isHating);
      }
  return (
    <textarea className="onion ${this.isHating}" onChange={onChangeListener}></textarea>    
    )
  }
}

export default OnionHater;
