import React from 'react';
import '../stylesheets/App.css';
import '../stylesheets/reset.css';
import Form from './Form';
import Card from './Card';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      description: '',
      language: '',
      age: '',
      genres: {
        genre1: '',
        genre2: '',
        genre3:''
      }
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleGender = this.handleGender.bind(this);

  }
    handleChange(event) {
      this.setState({ [event.target.id]: event.target.value });
      console.log(event.target.id, event.target.value);
      console.log(this.state);
    }

    handleGender(event){
      if(this.state.genres.genre1 === '' && event.target.value !== this.state.genres.genre1){
        this.setState({ [this.genres.genre1]: event.target.value });
      } else if(this.state.genres.genre2 === '' && event.target.value !== this.state.genres.genre2) {
        this.setState({ [genres.genre2]: event.target.value });
      } else if(this.state.genres3 === '' && event.target.value !== this.state.genres.genre3) {
        this.setState({ [genres.genre3]: event.target.value });
      }
    }

  render() {
    return <div className="App">
      <Form info={this.state} handleChange={this.handleChange} handleGender={this.handleGender}/>
      <Card info={this.state}/>
    </div>;
  }
}

export default App;
