import React from 'react';
import Form from './Form';

const numbers = [1, 4, 6, 8, 45, 40, 50, 60, 89];
let filteredNumber = [];
const NumberList = () => {

  const handleNumber = filterNumber => {
    numbers.filter(n => n > filterNumber);
  }

  const handleEven = () => {
    filteredNumber.filter(n => n % 2 === 0);
    // const result = evenNumbers.map((n, index) => <li key={index}>{n}</li> );
  }

  const result = () => {handleEven ? (handleEven) : (handleNumber)}


  return (
    <div>
      <Form handleNumber={handleNumber} handleEven={handleEven} result={result}/>
    </div>

  )
}

export default NumberList;
