import React from 'react';
import Form from './Form';

const numbers = [1, 4, 6, 8, 45, 40, 50, 60, 89];
let filteredNumber = [];
let evenNumbers = [];
const NumberList = props => {

  const handleNumber = filterNumber => {
    numbers.filter(n => n > filterNumber);
  }

  function handleEven() {
    evenNumbers = filteredNumber.filter(n => n % 2 === 0);
    const result = evenNumbers.map((n, index) => <li key={index}>{n}</li> );
    console.log(evenNumbers, result);
  }

  const result = () => { evenNumbers ? (
    evenNumbers.map((n, index) => <li key={index}>{n}</li> )
  ) : (
    handleNumber.map((n, index) => <li key={index}>{n}</li> )
  );
  }

  return (
    <div>
      <Form handleNumber={handleNumber} handleEven={handleEven}/>
      <ul>{props.result}</ul>
    </div>

  )
}

export default NumberList;
