import React, {useState} from 'react';
import Form from './Form'; 

const numbers = [1, 4, 6, 8, 45, 40, 50, 60, 89];
let filteredNumber = [];
let numbersEven = [];

const NumberList = () => {
  const [result, setResult] = useState(numbers);

  const handleNumber = filterNumber => {
    setResult(numbers.filter(n => n > filterNumber));
  }

  const handleEven = () => {
    setResult(filteredNumber.filter(n => n % 2 === 0));
  }

  // const result = () => {
  //   if (numbersEven === [] ) {
  //   this.numbersEven.map((n, index) => <li key={index}>{n}</li> );
  //   }else {
  //     this.filteredNumber.map((n, index) => <li key={index}>{n}</li> );
  //   }
  // }
  return (
  <div>
    <Form handleNumber={handleNumber} handleEven={handleEven} result={result}/>
  </div>
  )
}

export default NumberList;
