import React from 'react';
import Form from './Form';

const numbers = [1, 4, 6, 8, 45, 40, 50, 60, 89];
let filteredNumber = [];
let evenNumbers = [];
const NumberList = () => {

  function handleNumber(filterNumber) {
    filteredNumber = numbers.filter(n => n > filterNumber);
    console.log(filteredNumber);
  }

  function handleEven() {
    evenNumbers = filteredNumber.filter(n => n % 2 === 0);
    console.log(evenNumbers);
  }

  return (
    <ul>
      <Form handleNumber={handleNumber} handleEven={handleEven}/>
    </ul>
  )
}

export default NumberList;
