import React from 'react';
import Form from './Form';

const Item = props => {
  const numbers = [1, 4, 6, 8, 45, 89];
  // const incrementedAndEvenNumbers = numbers
  //   .map(n => n + 1)
  //   .filter(n => n > {bigNumber} === 0);

const handleNumber = bigNumber => {
  numbers
  .map(n => n + 1)
  .filter(n => n > {bigNumber});
  };

  const result = handleNumber.map(n => {
    return  <li>{n}</li>;
    }
  )


  return (
  <div>
    <ul className="cardList">
      {result}
      <Form handleNumber={handleNumber}/>
    </ul>
  </div>
  );


}

export default Item;
